void foo() { }
void bar() { }

void run() {
  thread t1( foo );
  // thread t2 = t1; // Se fizer isso vai gerar erro de compilação

  // usar move para transferir a posse da thread
  thread t2 = std::move( t1 ); // t1 não é mais dono da thread
  t1 = thread( bar ); // t1 tem nova thread

  t1.join();
  t2.join();
}

